/*
 * ConnectDlg creator
 */

bool ConnectDlg::Create( wxWindow* parent, wxWindowID id, const wxString& caption, const wxPoint& pos, const wxSize& size, long style )
{
	wxASSERT(!m_ip.IsNull());
	wxIPV4address ipAddr;
	ipAddr.Hostname(m_ip);
	ipAddr.Service(m_port);
	if (!m_client.Connect(ipAddr))
	{
		wxMessageBox("Connect failue.");
		return this->Destroy();
	}

////@begin ConnectDlg creation
    SetExtraStyle(wxWS_EX_VALIDATE_RECURSIVELY|wxWS_EX_BLOCK_EVENTS);
    wxDialog::Create( parent, id, caption, pos, size, style );

    CreateControls();
    if (GetSizer())
    {
        GetSizer()->SetSizeHints(this);
    }
    Centre();
////@end ConnectDlg creation

    return true;
}


