void ConnectDlg::CreateControls()
{    
////@begin ConnectDlg content construction
    // Generated by DialogBlocks, 02/06/2015 12:44:41 (unregistered)

    ConnectDlg* itemDialog1 = this;

    wxBoxSizer* itemBoxSizer2 = new wxBoxSizer(wxVERTICAL);
    itemDialog1->SetSizer(itemBoxSizer2);

    m_txContent = new wxTextCtrl( itemDialog1, ID_TEXT_CONTENT, wxEmptyString, wxDefaultPosition, wxSize(300, 200), wxTE_MULTILINE );
    itemBoxSizer2->Add(m_txContent, 0, wxALIGN_CENTER_HORIZONTAL|wxALL, 5);

    m_btnSend = new wxButton( itemDialog1, ID_BTN_SEND, _("Send"), wxDefaultPosition, wxDefaultSize, 0 );
    itemBoxSizer2->Add(m_btnSend, 0, wxALIGN_RIGHT|wxALL, 5);

////@end ConnectDlg content construction
}


