/*
 * wxEVT_COMMAND_BUTTON_CLICKED event handler for ID_BTN_SEND
 */

void ConnectDlg::OnBtnSendClick( wxCommandEvent& event )
{
	wxString content = m_txContent->GetValue();
	m_client.Write(content.mb_str(), content.Length());
	if (m_client.Error())
	{
		wxLogMessage("Send message error");
	}
	else
	{
		wxLogMessage("Send a message: %s", content);
	}
////@begin wxEVT_COMMAND_BUTTON_CLICKED event handler for ID_BTN_SEND in ConnectDlg.
    // Before editing this code, remove the block markers.
    event.Skip();
////@end wxEVT_COMMAND_BUTTON_CLICKED event handler for ID_BTN_SEND in ConnectDlg. 
}


